import React, { useState } from 'react';
import axios from 'axios';
import swal from 'sweetalert2';

const NewEventWindow = ({ onClose, onSubmit }) => {
  const [eventData, setEventData] = useState({
    label: '',
    dateStart: '',
    dateEnd: '',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setEventData({ ...eventData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    axios
      .post('http://localhost:5000/event/api/events', eventData)
      .then((response) => {
        // Show success message
        swal.fire({
          title: 'Data submitted',
          icon: 'success',
          confirmButtonText: 'OK',
        });
        // Pass the new event data back to the parent component
        onSubmit(eventData);
        // Close the window
        onClose();
      })
      .catch((error) => {
        console.error('Error saving event data:', error);
        // Show error message
        swal.fire({
          title: 'Error',
          text: 'Failed to save event data.',
          icon: 'error',
          confirmButtonText: 'OK',
        });
      });
  };

  return (
    <div className="new-event-window">
      <form onSubmit={handleSubmit}>
        {/* Input fields for each event property */}
        <label>
          Label:
          <input type="text" name="label" value={eventData.label} onChange={handleInputChange} />
        </label>
        <label>
          Start Date:
          <input
            type="date"
            name="dateStart"
            value={eventData.dateStart}
            onChange={handleInputChange}
          />
        </label>
        <label>
          End Date:
          <input
            type="date"
            name="dateEnd"
            value={eventData.dateEnd}
            onChange={handleInputChange}
          />
        </label>
        {/* Add more input fields for other event properties as needed */}
        <div className="button-group">
          <button type="submit">Submit</button>
          <button type="button" onClick={onClose}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default NewEventWindow;
