import 'smart-webcomponents-react/source/styles/smart.default.css';
import axios from 'axios';
import swal from 'sweetalert2';

function createCard(event) {
	const card = document.createElement('div');
	card.innerHTML = `
	  <h3>${event.label}</h3>
	  <p>${event.description}</p>
	  <p>Start: ${event.dateStart}</p>
	  <p>End: ${event.dateEnd}</p>
	  <img src="${event.image}" alt="Event Image" />
	  <p>Speaker(s): ${event.speaker.join(', ')}</p>
	`;
	card.classList.add('event-card');
	card.style.backgroundColor = event.backgroundColor;
	return card;
}

function createInput(label, value, onChange) {
	const inputContainer = document.createElement("div");
	const inputLabel = document.createElement("label");
	inputLabel.innerText = label;

	const input = document.createElement("input");
	input.type = "text";
	input.value = value;
	input.addEventListener("change", onChange);

	inputContainer.appendChild(inputLabel);
	inputContainer.appendChild(input);

	return inputContainer;
  }

  // Custom method to create textarea fields
 export function createTextArea(label, value, onChange) {
	const textareaContainer = document.createElement("div");
	const textareaLabel = document.createElement("label");
	textareaLabel.innerText = label;

	const textarea = document.createElement("textarea");
	textarea.value = value;
	textarea.addEventListener("change", onChange);

	textareaContainer.appendChild(textareaLabel);
	textareaContainer.appendChild(textarea);

	return textareaContainer;
  }
