import React, { useState } from 'react';

const NewEventForm = ({ onFormSubmit }) => {
  // Initial form state
  const initialFormData = {
    label: '',
    dateStart: '',
    dateEnd: '',
    // Add more properties for each event as needed
  };

  // State to manage the form data
  const [formData, setFormData] = useState(initialFormData);

  // Event handler to update the form data state when the user types into input fields
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Call the parent component's onFormSubmit function with the form data
    onFormSubmit(formData);
    // Reset the form data to initial state after submission
    setFormData(initialFormData);
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Input fields for each event property */}
      <label>
        Label:
        <input
          type="text"
          name="label"
          value={formData.label}
          onChange={handleInputChange}
        />
      </label>
      <label>
        Start Date:
        <input
          type="date"
          name="dateStart"
          value={formData.dateStart}
          onChange={handleInputChange}
        />
      </label>
      <label>
        End Date:
        <input
          type="date"
          name="dateEnd"
          value={formData.dateEnd}
          onChange={handleInputChange}
        />
      </label>
      {/* Add more input fields for other event properties as needed */}
      <button type="submit">Submit</button>
    </form>
  );
};

export default NewEventForm;
