import React, { useState } from 'react';

const NewEventsForm = ({ onSubmit }) => {
  const [eventData, setEventData] = useState({
    label: '',
    dateStart: '',
    dateEnd: '',
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setEventData({ ...eventData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit(eventData);
    setEventData({
      label: '',
      dateStart: '',
      dateEnd: '',
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Input fields for each event property */}
      <label>
        Label:
        <input type="text" name="label" value={eventData.label} onChange={handleInputChange} />
      </label>
      <label>
        Start Date:
        <input
          type="date"
          name="dateStart"
          value={eventData.dateStart}
          onChange={handleInputChange}
        />
      </label>
      <label>
        End Date:
        <input type="date" name="dateEnd" value={eventData.dateEnd} onChange={handleInputChange} />
      </label>
      {/* Add more input fields for other event properties as needed */}
      <button type="submit">Submit</button>
    </form>
  );
};

export default NewEventsForm;
