import React, { useState } from 'react';
import axios from 'axios';
import swal from 'sweetalert2';

const NewEventsForm = () => {
  const [eventData, setEventData] = useState({
    label: '',
    date: '',
    dateEnd: '',
    // Add more properties for each event as needed
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setEventData({ ...eventData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Assuming you have your backend route for creating events at '/api/events'
    axios
      .post('http://localhost:5000/event/api/events', eventData)
      .then((response) => {
        console.log(response.data);
        // Show success message
        swal.fire({
          title: 'Data submitted',
          icon: 'success',
          confirmButtonText: 'OK',
        });
        // Reset the form after successful submission
        setEventData({
          label: '',
          dateStart: '',
          dateEnd: '',
          // Add more properties for each event as needed
        });
      })
      .catch((error) => {
        console.error('Error saving event data:', error);
        // Show error message
        swal.fire({
          title: 'Error',
          text: 'Failed to save event data.',
          icon: 'error',
          confirmButtonText: 'OK',
        });
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Input fields for each event property */}
      <label>
        Label:
        <input
          type="text"
          name="label"
          value={eventData.label}
          onChange={handleInputChange}
        />
      </label>
      <label>
        Start Date:
        <input
          type="date"
          name="dateStart"
          value={eventData.dateStart}
          onChange={handleInputChange}
        />
      </label>
      <label>
        End Date:
        <input
          type="date"
          name="dateEnd"
          value={eventData.dateEnd}
          onChange={handleInputChange}
        />
      </label>
      {/* Add more input fields for other event properties as needed */}
      <button type="submit">Submit</button>
    </form>
  );
};

export default NewEventsForm;
