    .then((response) => {
      console.log(response.data);
      // Show success message
      swal.fire({
        title: 'Data submitted',
        icon: 'success',
        confirmButtonText: 'OK',
      });
      // Close the form and reset newEventData
      this.setState({
        showNewEventForm: false,
        newEventData: null,
      });
    })
    .catch((error) => {
      console.error('Error saving event data:', error);
      // Show error message
      swal.fire({
        title: 'Error',
        text: 'Failed to save event data.',
        icon: 'error',
        confirmButtonText: 'OK',
      });
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Input fields for each event property */}
      <label>
        Label:
        <input type="text" name="label" value={eventData.label} onChange={handleInputChange} />
      </label>
      <label>
        Start Date:
        <input type="date" name="dateStart" value={eventData.dateStart} onChange={handleInputChange} />
      </label>
      <label>
        End Date:
        <input type="date" name="dateEnd" value={eventData.dateEnd} onChange={handleInputChange} />
      </label>
      {/* Add more input fields for other event properties as needed */}
      <button type="submit">Submit</button>
    </form>
  );
};

export default NewEventsForm;
