//New calendar component
import 'smart-webcomponents-react/source/styles/smart.default.css';
import React from "react";
import ReactDOM from 'react-dom/client';
import { Button, RepeatButton, ToggleButton, PowerButton } from 'smart-webcomponents-react/button';
import { Calendar } from 'smart-webcomponents-react/calendar';
import { Input } from 'smart-webcomponents-react/input';
import { Tree, TreeItem, TreeItemsGroup } from 'smart-webcomponents-react/tree';
//import { Scheduler } from 'smart-webcomponents-react/scheduler';
import { Scheduler, Smart } from 'smart-webcomponents-react/scheduler';
import { SmartButton } from 'smart-webcomponents-react/button';
import { SmartCheckBox } from 'smart-webcomponents-react/checkbox';
import { SmartDateTimePicker } from 'smart-webcomponents-react/datetimepicker';




class App extends React.Component {
	constructor(props) {
		super(props);

		this.scheduler = React.createRef();
		this.calendar = React.createRef();
		this.tree = React.createRef();
		this.primaryContainer = React.createRef();

		const today = new Date(),
			currentDate = today.getDate(),
			currentYear = today.getFullYear(),
			currentMonth = today.getMonth(),
			currentHours = today.getHours(),
			currentMinutes = today.getMinutes()			

	}

	view = 'month';

	views = ['day',
		{
			type: 'week',
			hideWeekend: true,
		},
		{
			type: 'month',
			hideWeekend: true,
		}, 'agenda',

	];

	firstDayOfWeek = 1;

	disableDateMenu = true;

	currentTimeIndicator = true;

	scrollButtonsPosition = 'far';

	getPastThreeWeekdays(weekday) {
		let weekdays = [];

		for (let i = 0; i < 3; i++) {
			weekdays.push((weekday - i + 7) % 7);
		}

		return weekdays;
	}

	updateData(event) {
		const item = event.detail.item,
			data = this.data;

		for (let i = 0; i < data.length; i++) {
			const dataItem = data[i];

			if (dataItem.label === item.label && dataItem.class === item.class) {
				event.type === 'itemRemove' ? this.data.splice(i, 1) : data.splice(i, 1, item);
				return;
			}
		}
	}

	handleToggle() {
		const primaryContainer = this.primaryContainer.current,
			scheduler = this.scheduler.current;

		primaryContainer.classList.toggle('collapse');
		scheduler.disableDateMenu = !primaryContainer.classList.contains('collapse');
	}

	addNew() {
		this.scheduler.current.openWindow({
			class: 'event'
		});
	}

	handleCalendarChange(event) {
		this.scheduler.current.dateCurrent = event.detail.value;
	}

	handleTreeChange() {
    const tree = this.tree.current;
    const selectedIndexes = tree.selectedIndexes;
    const promises = [];

    for (let i = 0; i < selectedIndexes.length; i++) {
      promises.push(tree.getItem(selectedIndexes[i]));
    }

    Promise.all(promises)
      .then(results => {
        const types = results.map(result => result.value);
        this.scheduler.current.dataSource = this.data.filter(d => types.indexOf(d.class) > -1);
      })
      .catch(error => {
        // Handle any errors that occur during the Promise.all operation
      });
  }

  handleDateChange(event) {
    const selectedDate = event.detail.value;
    console.log('Selected date:', selectedDate);
  }

  createCard(event) {
	const card = document.createElement('div');
	card.innerHTML = `
	  <h3>${event.label}</h3>
	  <p>${event.description}</p>
	  <p>Start: ${event.dateStart}</p>
	  <p>End: ${event.dateEnd}</p>
	  <img src="${event.image}" alt="Event Image" />
	  <p>Speaker(s): ${event.speaker.join(', ')}</p>
	`;
	card.classList.add('event-card');
	card.style.backgroundColor = event.backgroundColor;
	return card;
  }

  //New Window
  customizeSchedulerWindow(target, type, eventObj) {
	// If the window is of type 'confirm', return (do not customize)
	if (type) {
	  return;
	}

	// Customize the appearance of the window
	target.footerPosition = 'bottom';
	target.label = eventObj.label ? eventObj.label : 'Events';
	target.maximizeButton = true;
	target.minimizeButton = true;
	target.buttonsAlignment = 'right';

	let mainPanel = target.querySelector('.smart-main-panel');
	let repeatingPanel = target.querySelector('.smart-repeating-panel');
	let okButton = target.querySelector('.smart-dialog-footer .smart-ok');
	let cancelButton = target.querySelector('.smart-dialog-footer .smart-cancel');

	if (!mainPanel) {
	  // Create and add the main panel
	  mainPanel = document.createElement('div');
	  mainPanel.classList.add('smart-main-panel');
	  target.appendChild(mainPanel);

	  // Create editors for main properties
	  const labelEditor = document.createElement('smart-input');
	  labelEditor.placeholder = 'Enter a label...';
	  labelEditor.value = eventObj.label || '';
	  labelEditor.addEventListener('change', (event) => {
		eventObj.label = event.detail.value;
	  });

	  const descriptionEditor = document.createElement('smart-input');
	  descriptionEditor.placeholder = 'Enter a description for the event...';
	  descriptionEditor.value = eventObj.description || '';
	  descriptionEditor.addEventListener('change', (event) => {
		eventObj.description = event.detail.value;
	  });

	  const dateStartEditor = document.createElement('smart-datetime-picker');
	  dateStartEditor.value = eventObj.dateStart;
	  dateStartEditor.addEventListener('change', (event) => {
		eventObj.dateStart = event.detail.value;
	  });

	  const dateEndEditor = document.createElement('smart-datetime-picker');
	  dateEndEditor.value = eventObj.dateEnd;
	  dateEndEditor.addEventListener('change', (event) => {
		eventObj.dateEnd = event.detail.value;
	  });

	  // Append editors to the main panel
	  mainPanel.appendChild(labelEditor);
	  mainPanel.appendChild(descriptionEditor);
	  mainPanel.appendChild(dateStartEditor);
	  mainPanel.appendChild(dateEndEditor);
	}

	// Hide the repeating panel since it is not used in this custom window
	repeatingPanel.style.display = 'none';

	// Modify the Ok and Cancel buttons to apply and close the window, respectively
	okButton.textContent = 'Apply';
	cancelButton.textContent = 'Close';
	okButton.addEventListener('click', () => {
	  this.scheduler.current.closeWindow();
	});
	cancelButton.addEventListener('click', () => {
	  this.scheduler.current.closeWindow();
	});
  }



	init() {
	}

	componentDidMount() {
		this.init();
	}

	render() {
		return (
             <div style={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '1px', backgroundColor: '#FFFFFF'}}>
                <h1 style={{ padding: '5px', backgroundColor: '#FFFFFF', color: 'black', borderRadius: '4px', cursor: 'pointer',height:'1px' }}>Academic Calendar</h1>
                <Button id="addNew" className="floating" onClick={this.addNew.bind(this)} style={{ padding: '8px',width:'200px', backgroundColor: '#f5f5f5', color: 'black', borderRadius: '4px', cursor: 'pointer' }}>
                  <span>New Event</span>
                </Button>
              </div>
              <div style={{ display: 'flex', flexGrow: 1 }}>

						<section style={{ width: '100%', padding: '10px'}}>
                  <div style={{ height: '100%', backgroundColor: '#f5f5f5' }}>
							<Scheduler ref={this.scheduler} id="scheduler" style={{height:'500px'}} dataSource={this.data} view={this.view} views={this.views} nonworkingDays={this.nonworkingDays}
								firstDayOfWeek={this.firstDayOfWeek}
								currentTimeIndicator={this.currentTimeIndicator}
								scrollButtonsPosition={this.scrollButtonsPosition} onDragEnd={this.updateData.bind(this)}
								onResizeEnd={this.updateData.bind(this)} onItemUpdate={this.updateData.bind(this)}
								onItemRemove={this.updateData.bind(this)} onDateChange={this.handleDateChange.bind(this)} allowResizing={true} windowCustomizationFunction={this.customizeSchedulerWindow.bind(this)} ></Scheduler>
                </div>
						</section>
					</div>
				</div>

		);
	}
}




export default App;
