import React, { useState } from 'react';
import { Input } from 'smart-webcomponents-react/input';
import { Button } from 'smart-webcomponents-react/button';

const NewEventsForm = ({ onSubmit }) => {
  const [eventsData, setEventsData] = useState([
    { label: '', dateFrom: new Date(), dateTo: new Date() },
  ]);

  const handleInputChange = (event, index) => {
    const { name, value } = event.target;
    const updatedEventsData = [...eventsData];
    updatedEventsData[index][name] = value;
    setEventsData(updatedEventsData);
  };

  const addNewEvent = () => {
    setEventsData([...eventsData, { label: '', dateFrom: new Date(), dateTo: new Date() }]);
  };

  const removeEvent = (index) => {
    const updatedEventsData = [...eventsData];
    updatedEventsData.splice(index, 1);
    setEventsData(updatedEventsData);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit(eventsData);
  };

  return (
    <form onSubmit={handleSubmit}>
      {eventsData.map((event, index) => (
        <div key={index}>
          <Input
            name="label"
            label="Event Label"
            value={event.label}
            onChange={(e) => handleInputChange(e, index)}
          />
          <Input
            name="dateFrom"
            label="Date From"
            type="date"
            value={event.dateFrom}
            onChange={(e) => handleInputChange(e, index)}
          />
          <Input
            name="dateTo"
            label="Date To"
            type="date"
            value={event.dateTo}
            onChange={(e) => handleInputChange(e, index)}
          />
          {index > 0 && (
            <Button variant="danger" onClick={() => removeEvent(index)}>
              Remove Event
            </Button>
          )}
          <hr />
        </div>
      ))}
      <Button onClick={addNewEvent}>Add New Event</Button>
      <Button type="submit">Submit</Button>
    </form>
  );
};

export default NewEventsForm;
