//New calendar component
import 'smart-webcomponents-react/source/styles/smart.default.css';
import React from "react";
import ReactDOM from 'react-dom/client';
import { Button, RepeatButton, ToggleButton, PowerButton } from 'smart-webcomponents-react/button';
import { Calendar } from 'smart-webcomponents-react/calendar';
import { Input } from 'smart-webcomponents-react/input';
import { Tree, TreeItem, TreeItemsGroup } from 'smart-webcomponents-react/tree';
import { Scheduler } from 'smart-webcomponents-react/scheduler';



class App extends React.Component {
	constructor(props) {
		super(props);

		this.scheduler = React.createRef();
		this.calendar = React.createRef();
		this.tree = React.createRef();
		this.primaryContainer = React.createRef();

		const today = new Date(),
			currentDate = today.getDate(),
			currentYear = today.getFullYear(),
			currentMonth = today.getMonth(),
			currentHours = today.getHours(),
			currentMinutes = today.getMinutes()			

	}

	view = 'month';

	views = ['day',
		{
			type: 'week',
			hideWeekend: true,
		},
		{
			type: 'month',
			hideWeekend: true,
		}, 'agenda',

	];

	firstDayOfWeek = 1;

	disableDateMenu = true;

	currentTimeIndicator = true;

	scrollButtonsPosition = 'far';

	getPastThreeWeekdays(weekday) {
		let weekdays = [];

		for (let i = 0; i < 3; i++) {
			weekdays.push((weekday - i + 7) % 7);
		}

		return weekdays;
	}

	updateData(event) {
		const item = event.detail.item,
			data = this.data;

		for (let i = 0; i < data.length; i++) {
			const dataItem = data[i];

			if (dataItem.label === item.label && dataItem.class === item.class) {
				event.type === 'itemRemove' ? this.data.splice(i, 1) : data.splice(i, 1, item);
				return;
			}
		}
	}

	handleToggle() {
		const primaryContainer = this.primaryContainer.current,
			scheduler = this.scheduler.current;

		primaryContainer.classList.toggle('collapse');
		scheduler.disableDateMenu = !primaryContainer.classList.contains('collapse');
	}

	addNew() {
		this.scheduler.current.openWindow({
			class: 'event'
		});
	}

	handleCalendarChange(event) {
		this.scheduler.current.dateCurrent = event.detail.value;
	}

	handleTreeChange() {
    const tree = this.tree.current;
    const selectedIndexes = tree.selectedIndexes;
    const promises = [];

    for (let i = 0; i < selectedIndexes.length; i++) {
      promises.push(tree.getItem(selectedIndexes[i]));
    }

    Promise.all(promises)
      .then(results => {
        const types = results.map(result => result.value);
        this.scheduler.current.dataSource = this.data.filter(d => types.indexOf(d.class) > -1);
      })
      .catch(error => {
        // Handle any errors that occur during the Promise.all operation
      });
  }

	handleDateChange(event) {
		this.calendar.current.selectedDates = [event.detail.value];
	}

	init() {
	}

	componentDidMount() {
		this.init();
	}

	render() {
		return (
             <div style={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
              {/* <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '10px', backgroundColor: '#f5f5f5' }}>
                <Button style={{ padding: '8px', backgroundColor: '#f5f5f5', color: 'black', borderRadius: '4px', cursor: 'pointer' }}></Button>
                <Button id="addNew" className="floating" onClick={this.addNew.bind(this)} style={{ padding: '8px',width:'200px', backgroundColor: '#f5f5f5', color: 'black', borderRadius: '4px', cursor: 'pointer' }}>
                  <span>Create</span>
                </Button>
              </div> */}
              <div style={{ display: 'flex', flexGrow: 1,height:'100%' }}>

						<section style={{ width: '100%', padding: '10px',height:'100%' }}>
                  <div style={{ height: '100%', backgroundColor: '#f5f5f5' }}>
							<Scheduler ref={this.scheduler} id="scheduler" style={{height:'600px'}} dataSource={this.data} view={this.view} views={this.views} nonworkingDays={this.nonworkingDays}
								firstDayOfWeek={this.firstDayOfWeek}
								currentTimeIndicator={this.currentTimeIndicator}
								scrollButtonsPosition={this.scrollButtonsPosition} onDragEnd={this.updateData.bind(this)}
								onResizeEnd={this.updateData.bind(this)} onItemUpdate={this.updateData.bind(this)}
								onItemRemove={this.updateData.bind(this)} onDateChange={this.handleDateChange.bind(this)} allowResizing={true} headerTemplate={(headerContainer) => {
									headerContainer.innerHTML = `
									<div style="display: flex; justify-content: space-between; padding: 10px; background-color: #f5f5f5;">
									<Button style="padding: 8px; background-color: #f5f5f5; color: black; border-radius: 4px; cursor: pointer;">Today</Button>
									<div>
									  <SmartButton id="addNew" className="floating" onClick=${this.addNew.bind(this)} style="padding: 8px; width: 200px; background-color: #f5f5f5; color: black; border-radius: 4px; cursor: pointer;">
										<span>Create</span>
									  </SmartButton>
									<SmartDropDownButton id="dateSelector" style="margin-left: 10px;">
									<div>Selected Date</div>
									<SmartCalendar></SmartCalendar>
								  </SmartDropDownButton>
								  <SmartDropDownButton id="viewSelector" view=${this.view} views=${this.views} style="margin-left: 10px;">
									<div>Selected View</div>
									<SmartMenu>
									  <SmartMenuItem value="day">Day</SmartMenuItem>
									  <SmartMenuItem value="week">Week</SmartMenuItem>
									  <SmartMenuItem value="month">Month</SmartMenuItem>
									</SmartMenu>
								  </SmartDropDownButton>
								</div>
							  </div>
							`;
								  }}></Scheduler>
                </div>
						</section>
					</div>
				</div>

		);
	}
}




export default App;
